secureScript.script:
import hudson.model.*
import jenkins.model.Jenkins
import static javax.xml.xpath.XPathConstants.*
import groovy.xml.DOMBuilder
import groovy.xml.dom.DOMCategory

def jenkinsInstance = Jenkins.getInstance()
def jobName = this.binding.jenkinsProject.name
def customer = jobName.split("-")[1].trim()
def item = jenkinsInstance.getItemByFullName(jobName)

def config = item.getConfigFile()
def file = config.getFile()
def fileContents = file.getText('UTF-8')
def doc = DOMBuilder.parse(new StringReader(fileContents), false, false).documentElement
def workspace = ""

use(DOMCategory) {
	workspace = doc.xpath( "/project/customWorkspace", NODESET).text()
}
if (workspace == "") {
	workspace = jenkinsInstance.getWorkspaceFor(item)
}

evaluate(new File(workspace  + "/ECOManageRobotProject/Scripts/" + customer + "_GetTestSuites.groovy"))

